css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
*
  {
  }
body, html {
     background-position: top;
     margin: auto;
     margin-top: auto;
     margin-top: 10px;
     background-color: black;
     width: 1080px;
          }   
html {
    background-color: black;
    background-position: center top;
    margin: auto;
    margin-top: auto;
    margin-top: 10px;
}
nav div.primary {
     background-color: transparent;
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/621597089679278090/brickhubbanner.png');
     padding-top: 5.5%;
     overflow: hidden;
     background-repeat: no-repeat;
     width: absolute;
 }
nav .info {
    top: 99px;
    position: absolute;
    right: 0px;
    background-color: #515151;
    width: 290px;
}
.card .top:not(.absolute), .card .top:not(.absolute).blue, .card .top:not(.absolute).green, .card .top:not(.absolute).orange, .card .top:not(.absolute).red {
    background-color: #FF7F00;
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.username {
    float: right;
    margin: 5px 20px 20px 20px;
    color: white;
    text-align: right;
    padding: 2px;
    padding-right: 50px;
    position: absolute;
    top: 62px;
    left: 86%;
    width: 170px;
   }
}
