css:
@media only screen and (max-width: 800px) {
	.aui-navgroup, #header, #add-page-tree-dialog-really-save-dialog, #add-page-tree-dialog-really-undo-dialog, #main-header, #footer, .task-summary-label {
		display: none !important;
	}
    
    .aui-button {
        font-size: 10px !important;
        padding: 2px 5px !important;
         
    }
    
    table tr th:nth-child(2), table tr th:nth-child(3) {
        display: none !important;                               
    }
    
    table tr td:nth-child(2), table tr td:nth-child(3) {
        display: none !important;                               
    }
    
    #main {
       height: 100% !important;
    }
    
    #mytask-controls {
       margin-bottom: 0px !important;                                
    }

    .wiki-content a.user-mention.current-user-mention {
       background-color: /*[[currentUserMention]]*/ !important;
    }
}
