css:
@-moz-document domain("v.huya.com") {
#play2 {
  max-width: 95vw;
}

#play2 .crumb {
  display: none;
}

#play2 .content {
  border-right: unset;
}

#play2 .content .content-aside {
  float: unset;
}

.info-comment {
  display: none;
}

#play2 .content .content-main {
  display: flex;
  justify-content: center;
}

.main-vplayer {
  width: calc(100% - 300px);
}

#play2 .content .content-main .main-info {
  display: flex;
  align-self: flex-start;
  margin-top: unset;
  width: 250px;
  margin-left: 10px;
}

#play2 .content .content-main .main-info .info-video {
  display: flex;
  flex-direction: column;
  border-right: unset;
  width: 100%;
  align-items: center;
}

.detail-desc,
.detail-button {
  display: none;
}

#play2 .content .content-main .main-info .info-video .video-detail {
  border-right: unset;
}

#play2 .content .content-main .main-info .info-video .video-author {
  padding-left: unset;
  margin-right: unset;
}
}
