css:
@-moz-document domain("greasyfork.org") {
pre.prettyprint, #ace-editor {
	font-family: "Operator Mono SSm Lig","Operator Mono Lig","Operator Mono SSm","Operator Mono",SFMono-Regular,Consolas,Liberation Mono,Menlo,monospace !important;
	font-size: 90%
}

pre.prettyprint .com, pre.prettyprint .kwd, #ace-editor .ace_comment, #ace-editor .ace_keyword:not(.ace_operator) {
	font-style: italic;
}
}
