css:
@-moz-document domain("vuejs.org") {
:root {
    --text-color: white;
}

body {
    color: var(--text-color);
    background-color: #303030;
}

h1, h2, h3, h4, strong {
    color: var(--text-color);
}

.content h2 a, .content h3 a, .content h2 button, .content h3 button {
    color: var(--text-color);
}
}
