css:
@-moz-document domain(drupal.org) {
@media(prefers-color-scheme: dark) {
  :root {
    --primary-font-color: #aaa;
    --secondary-font-color: #777;
    --background-color: #222;
    --table-zebra-color: #333;
    --table-hover-color: #444;
    --hover-color: #333;
  }
}
body {
  background: #222;
  color: #aaa;
}
a, a:visited, a:active {
  color: #aaa;
  text-decoration: underline;
}
h1, h2, h3 {
  color: #aaa;
}
pre[class*="language-"] {
  color: #ddd;
}
#nav-content ul li a {
  color: #aaa;
  text-decoration: none;
}
#nav-content li.active a.active, #nav-content li.active-parent a, #nav-content li a:hover, #nav-content li a:focus, #nav-content li.active-parent a {
  color: #333;
}
}
