css:
@-moz-document domain("campus.uvic.cat")
{
  html {
  		background-color: #1d1d1d !important;
  }

.container-fluid {
    background-color: #1d1d1d;
}
  
  .navbar {
    background-image: linear-gradient(#3d3d3d, #1d1d1d) !important;
    border: none !important;
}

.widgetS {
    border:none !important;
    background-color: #3d3d3d !important;
    color: white;
    border-radius: 8px;
    box-shadow: 0px 0px 20px #5d5d5d;
}

.footer{
    background-color: #1d1d1d;
    border: none !important;
}

.navbar-brand > img {
    content:url("https://ucampus.uvic-ucc.cat/assets/img/logoucampus.png");
    height: 30px;
}

.btn-default {
    background-color: #2d2d2d !important;
    border-color: #2d2d2d !important;
}

/* width */
::-webkit-scrollbar {
  width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #1d1d1d; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #888; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #555; 
}


#nav-drawer {
    background-color: #1d1d1d;

}

.list-group-item {
    background-color: #3d3d3d !important;
    color: lightgray !important;
    border-radius: 3px !important;
    margin-bottom: 3px !important;
    box-shadow: inset 0px 0px 10px #5d5d5d;
}

nav.fixed-top.navbar.navbar-light.bg-white.navbar-expand.moodle-has-zindex {
    background-image: linear-gradient(#1d1d1d, #3d3d3d) !important;
    margin-top: 11px;
}

span {
    color:lightgray !important;
}

a{
    color: lightgreen !important;
}

a:hover{
    color: green !important;
}

button.btn.nav-link.float-sm-left.mr-1.btn-light.bg-gray {
    background-color: #4d4d4d !important;
    border-radius: 5px;
    color: lightgray;
    border: none;
}

.icon {
    color: lightgray;
}

.card-body, .userprofile .profile_tree section {
    background-color: #3d3d3d;
    border-radius: 5px;
    color: lightgray;
}

.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    background-color: #1d1d1d;
    border: none !important;
}

div#coc-courselist {
    background-color: #3d3d3d;
}

#coc-filterlist {
    background-color: #3d3d3d;
}

select#coc-filterterm {
    background-color: #3d3d3d;
    color: lightgray;
    border-radius: 5px;
}

footer#page-footer {
    background-color: #1d1d1d !important;
}

.row.coc-hidden {
    background-color: #3d3d3d;
}

div#yui_3_17_2_1_1568987468587_123 {
    background-color: #3d3d3d;
}

.card-deck.dashboard-card-deck.one-row.fixed-width-cards.justify-content-center.overflow-hidden {
    background-color: #3d3d3d;
}

div#yui_3_17_2_1_1568987468587_124 {
    background-color: #3d3d3d;
}

.card-body, .userprofile .profile_tree section {
    background-color: #3d3d3d;
    border-radius: 5px;
    color: lightgray;
    box-shadow: inset 0px 0px 20px 0px #5d5d5d;
}

section#region-main {
    background-color: #3d3d3d;
    border-radius: 5px;
    box-shadow: inset 0px 0px 20px 0px #5d5d5d;
    color: lightgrey;
}

}
