css:
@-moz-document regexp("https://mvnrepository.com/.*")
{
#right, #left {
    display:none;
}
#page {
    background-color:#f1f1f1;
}
/*中间样式*/
 #maincontent {
    margin: 0 auto;
    width: 95%;
    border:none;
}
/* #maincontent > div:nth-child(17).version-section {
    display:none;
} */

/*Group列表插入公告隐藏*/
 #maincontent > div:nth-child(13).im {
    display:none;
}
/*推荐书隐藏*/
 #maincontent > table.grid + br+style+ div+div.version-section {
    display:none;
}
/* #maincontent > div.version-section */

/*坐标详情页面隐藏公告*/

/*div#snippets +script+ script+ br + div + br + div{
     display:none;
}*/

