} */

/* #maincontent > div:nth-child(11) > ins.adsbygoogle{
    display:none !important;
} */

/*坐标详情页面隐藏公告*/
 #maincontent > div:nth-child(11)[style="min-height: 120px"] {
    display:none !important;
}
/*坐标详情页面隐藏公告*/
 #maincontent > div:nth-child(13)[style="min-height: 0px !important; height: auto !important;"] {
    display:none !important;
}
