css:
@-moz-document domain("bryk.pl") {
/* Update v.1.1 */
.recommended {
    display: none !important;
}
.add-question .field-description {
    margin-bottom: 0 !important;
}


/* Removes sidebar */
.col-md-8{
     width: inherit !important;
}
.col-md-4{
     display: none !important;
}
/* Removes "Go to interia.pl" button on the top*/
.frillContainer{
    display: none !important;
}
/* Removes ads on top */
.advert-container {
    display: none !important;
}
/* Removes share buttons */
.share-fb-wrapper {
    display: none !important;
}
/* Makes "Add question" element less distractive */
.add-question-wrapper{
    padding: 20px !important;
    border: 0 !important;
}
.col-sm-9 h3{
    font-size: 30px !important;
    color: black !important;
}
/* Update v.1.1 - ".col-sm-3 a" changed to ".col-sm-3 .question-btn" + 5px of margin bottom and top has been added */
.col-sm-3 .question-btn{
    width: 180px !important;
    background: black !important;
    margin-top: 8px !important;
    margin-bottom: 8px !important;
}
.col-sm-3 a:hover{
    color: #fff586 !important;
}

/* Main page feed - more clean look */
.title{
    margin-top: 20px !important;
}
.addTaskModuleContainer{
    margin-bottom: 150px !important;
}

/* Optional */
.header-list {
    display: none !important;
}
.add-content-button-on-fixed {
    display: none !important;
}
li.list-btn.list-item__inline:before{
    visibility: hidden !important;
}
header.main-header.header-with-advert.fixed-header{
    visibility: hidden !important;
}
.col-xs-5{
    display: none !important;
}
/*update v1.1*/
.main-header nav {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
}
/*by mble*/
}
