css:
@-moz-document domain("linkedin.com") {
.header{
    background-color: #D3D3D3;
}

.results-context-header__query-search, .results-context-header__job-count, .control-bar, .results-context-header__new-jobs, .topcard, .profile-result-card__posted-by, .profile-result-card, .job-result-card__meta{
    display: none;
}

.description{
    background-color: #e6f7ff;
    margin: 0px;
    padding: 50px;
}

.results-context-header{
    border: none;
    padding: 0px;
}

.results{
    margin: 0px;
}

.results__detail-view{
    padding: 0px;
}

.results--two-pane{
    margin: auto;
}

.result-card{
    padding: 50px;
}

.nav{
    padding: 30px;
}
}
