css:
@-moz-document url-prefix("https://osu.ppy.sh/"), domain("osu.ppy.sh") {
/*V1.4_200616*/
u-page--generic, .osu-page--generic-compact, .osu-page--header, .user-home, .news-post-preview__body, .update-streams-v2, .osu-layout__row--page-compact, .osu-layout__row--page-compact-no-margin, .account-edit, .beatmapsets__sort, .beatmapset-panel__panel, .ranking-page-header, .ranking-page-table__row, .ranking-page-table__row:hover, .beatmapset-stats__row, .osu-page--generic, .osu-page--generic-compact, .osu-page--header, .beatmap-score-top__section, .beatmap-scoreboard-table__body-row--highlightable:nth-child(odd) .beatmap-scoreboard-table__cell, .comments, .forum-item, .osu-page--forum, .forum-topic-title, .forum-post-info, .osu-page--forum-topic-reply, .forum-post-edit__content, .forum-topic-entry, .profile-header, .play-detail, .beatmap-playcount__detail, .beatmap-playcount:hover .beatmap-playcount__detail, input, .search, .search-entry{
    background: rgba(0, 0, 0, 0.15);
}
.beatmap-scoreboard-table__body-row--highlightable .beatmap-scoreboard-table__cell, .play-detail__pp:before, .play-detail__score-detail, .play-detail__group--top {
    background: rgba(0,0,0,0.3);
}
/*[[bmi]]*/
.osu-page-header.osu-page-header--two-col.osu-page-header--home-user.js-current-user-cover, .page-extra-tabs-before, .page-mode, .nav2-header__legacy-triangles,  .osu-page-header--home-user:before, .osu-page-header--home-user .osu-page-header__box--status, .user-home__right-sidebar, .header-v4__bg-container, .header-v4__bg:before, .nav2-header__transition-overlay, .user-home__news-posts-group, .footer, .osu-page--users, .osu-page-header--home-user, .account-edit__input-group, .beatmapsets-search, .beatmapsets, .beatmapset-panel__image-overlay, .beatmapset-panel__header, .osu-page--rankings, .beatmap-success-rate, .comment-editor__message, .comment-editor, .comments__content, .comment, .nav2-header__menu-bg, .header-v4__row--title, .header-v4__row--bar, .forum-item-stripe:after, .forum-item:hover, .forum-topic-entry:hover, .osu-page--forum-topic, .profile-detail-bar, .profile-links, .page-extra-tabs--profile-page, .osu-layout__section--users-extra, .page-extra, .play-detail--active .play-detail__group--top, .play-detail:hover .play-detail__group--top, .play-detail--active .play-detail__score-detail, .play-detail:hover .play-detail__score-detail, .play-detail--active .play-detail__pp:before, .play-detail:hover .play-detail__pp:before, .play-detail--active, .play-detail:hover, .page-extra__recent-medals-box, .beatmap-playcount, .search-header, .search-result{
background: transparent!important;
}     
.user-home__right-sidebar {
    box-shadow: none;
}
a.news-post-preview__image, .user-home-beatmapset__cover, .beatmapset-panel__image, .bbcode img, .beatmap-playcount__cover {
   opacity: 0.8;
    filter: contrast(1.3);
}
.user-home__news-title, .news-post-preview__post-date, .osu-page-header__status-text, .user-home__beatmap-list-title, .user-home-beatmapset__playcount, .user-home__news-posts-group--more, .user-home-beatmapset:active, .user-home-beatmapset:focus, .user-home-beatmapset:hover, .footer, :focus~.account-edit-entry__label, .comment__row-item--username-link, .comment__action, .forum-item__link, .forum-topic-entry__link, .header-nav-v4__link, .forum-topic-title__post-time, .link--default, .bbcode a:active, .bbcode a:focus, .bbcode a:hover, .bbcode a, .profile-extra-entries__link, .profile-extra-entries__link:active, .profile-extra-entries__link:focus, .profile-extra-entries__link:hover, .profile-info__flag, .line-chart--profile-page .line-chart__tick-text, .beatmap-playcount__mapper, .beatmap-playcount__mapper-link, .user-session-list-session__last-active, .comment__message a, .comment__message a:active, .comment__message a:focus, .comment__message a:hover, .comment__message pre, .comments__title, .search-result__row--more, .search-result__row--more:active, .search-result__row--more:focus, .search-result__row--more:hover, .search-entry__row--footer, .profile-extra-entries__time{
    color: /*[[acn]]*/;
    fill: /*[[acn]]*/;
    -webkit-font-smoothing: antialiased;
}
.news-post-preview__post-date, .user-card--active.user-card--has-outline::after, .user-card:hover.user-card--has-outline::after, .osu-layout__sub-row--lg1-compact, .comment-editor, .nav2-header__menu-bg, .beatmapsets-search__input {
    border: transparent;
}
.header-v4__bg, .beatmapsets-search__background {
    mix-blend-mode: luminosity;
}
.user-home-beatmapset:hover, .user-card__background-overlay:hover, .account-edit, .beatmapset-header__content:before, .beatmap-score-top__section {
    background-image: linear-gradient(20deg, /*[[acn]]*/, transparent 50%)!important;
}
.page-extra {
    background-image: linear-gradient(20deg, /*[[acn]]*/, transparent 20%)!important;
}
.t-section .u-section--gradient-down, .play-detail--active .play-detail__group--top, .play-detail:hover .play-detail__group--top {
    background-image: linear-gradient(175deg, /*[[acn]]*/, transparent 30%)!important
}
.beatmapset-panel__panel, .beatmap-playcount:hover .beatmap-playcount__detail, input {
 background-image: linear-gradient(-20deg, /*[[acn]]*/, transparent 50%)!important;
}
.ranking-page-table__row {
    background-image: linear-gradient(20deg, /*[[acn]]*/, transparent 40%, /*[[acn]]*/)!important;
}
.forum-item:hover, .forum-topic-entry:hover {
    background-image: linear-gradient(-20deg, /*[[acn]]*/, transparent 20%)!important;
}
.user-home-beatmapset:hover, .user-card__background-overlay {
    background: rgba(0,0,0,0.01);
}
.fa-chevron-right:before {
    color: transparent;
}
.nav-button {
    border: 2.5px solid /*[[acn]]*/;
    opacity: 0.85;
    border-radius: 50%;
}
.user-home__beatmap-list-title, .user-home-beatmapset__meta, .user-home__news-title {
    font-weight: 550;
}
.simple-menu, .sticky-header__body, .beatmap-scoreboard-table__body-row--highlightable:hover:nth-child(odd) .beatmap-scoreboard-table__cell, .beatmap-scoreboard-table__body-row--highlightable:hover .beatmap-scoreboard-table__cell, .forum-post__body, .osu-page--news {
    background: rgba(0,0,0,0.75);
}
.account-edit-entry__label, .beatmapset-panel, .ranking-page-table__column--dimmed, .ranking-page-table__user-link-text, .ranking-page-table__country-link, .beatmap-scoreboard-table__header, .beatmap-score-top__stat-header, .comment__row--footer, .comment__row--footer a, .forum-item__description, .forum-topic-entry, .pagination-v2__link--link, .beatmapsets-search__input {
    color: #fff;
}
.beatmap-scoreboard-table__cell--perfect {
color:#00ff00;
}
a.news-post-preview__image {
    border-radius: 8px 8px 0 0;
}
.beatmap-playcount__detail {
    margin-left: 0;
    border-radius: 0 8px 8px 0;
}
.beatmap-playcount__cover {
    border-radius: 8px 0 0 8px;
}
.play-detail__pp-unit {
    color: #ff66ab;
}
.beatmapsets-search__input:focus {
    box-shadow: 0 0 10px /*[[acn]]*/;
}                                        
 .beatmapset-header__content {
    background-color: rgba(0,0,0,0)
}   
}
