css:
@-moz-document url-prefix("https://www.edsm.net/en/dashboard") {
nav {
    display: none;
    visibility: collapse;
}

#galacticMapping, footer, .card-footer {
    display: none;  
}

.card {
	border: 0!important;    
}

div.container:nth-child(4){
    max-width: 100%!important;
    padding-right: 0!important;
	padding-left: 0!important;
    position: absolute;
    top: 0;
}

#header-navbar {
    margin-bottom: 0!important;
}

#subNavigation {
    margin-top: 0!important;
    margin-bottom: 0!important;
}

body {
    line-height: 1!important;
}

}
