css:
@-moz-document domain("twitch.tv") {
body {
        font-family: Roboto, Helvetica Neue, Helvetica, Arial, sans-serif !important;
}
 
.tw-c-background-base{
    background-color:#4b367c !important;
}
 
.tw-root--theme-dark .navigation-link {
    color: #b19dd8;
}
 
.tw-root--theme-dark .navigation-link:hover {
    color: #fff;
}
 
.tw-root--theme-dark .top-nav__external-link{
    color: #b19dd8;
}
 
.tw-root--theme-dark .top-nav__external-link:hover{
    color: #fff;
}
 
.tw-input{
    background-color: #0f0e11;
    border: 1px solid #392e5c;
}
 
.tw-input:focus{
    background-color: #0f0e11;
    border: 1px solid #392e5c;
    border-color: #7d5bbe;
    box-shadow: 0 0 6px -2px #7d5bbe
}
 
.tw-core-button--secondary.tw-core-button--disabled{
    background-color: rgba(0,0,0,0.4);
    border: 1px solid #392e5c;
}
 
.tw-icon__svg {
    fill: #fff;
}
 
.tw-font-size-4 {
    font-size: 1.6rem!important;
}
 
.tw-root--theme-dark .channel-root__right-column,
.tw-root--theme-dark .chat-room {
    background: #191919 !important;
}
.channel-root.tw-full-height {
    background: #161616 !important;
}
.channel-leaderboard-header.tw-c-background-base.tw-relative {
    background: #141414 !important;
}
.root-scrollable__wrapper.tw-full-width.tw-relative {
    background: #161616 !important;
}
.tw-root--theme-dark {
    --color-background-input-focus: #161616 !important;
}
}
