css:
@-moz-document url-prefix("https://www.watch2gether.com/rooms/") {
.w2g-main-right {
    width: 0;
    min-width: 0;
}
.w2g-topbar {
    min-height: 42px;
}
.w2g-player-search {
	min-width: calc(728px + 1.5em);
	max-width: calc((100vh) * 1.77);
	max-height: 100%;
	margin-top: 0.75em;
	margin-right: 0.5em;
	flex-grow: 1;
	display: flex;
	flex-direction: column;
	position: relative;
}

.w2g-video-container {
	max-height: calc(100vh - 184px);
}
}
