css:
@-moz-document domain("thesoftwareguild.com")
{

body {
    background-color: rgb(42, 42, 42);
    color: rgb(228, 228, 228);
}
#lesson-content, .item-group-container, .item-group-condensed context_module student-view completed context_module_hover, .header-bar, .ig-row ig-published student-view {
    background-color: rgb(42, 42, 42);
    color: rgb(228, 228, 228);
}
span, nav, .ig-header .name, .item-group-condensed .ig-row, #breadcrumbs, .ic-app-main-content pre.prettyprint, element.style {
    background-color: rgb(42, 42, 42);
    color: rgb(228, 228, 228);
}
blockquote, .list-view>nav>ul>li>a, table.summary tbody th, table.summary td, table.summary tbody th, .ic-Table td, .quiz-submission, td, .question, #questions.assessment_results .question .header {
    background-color: rgb(42, 42, 42);
    color: rgb(228, 228, 228);
    border-color: rgb(228, 228, 228);
}
.ic-app-crumbs, .header-bar-left header-left-flex, .module-sequence-footer .module-sequence-footer-content {
    background-color: rgb(42, 42, 42);
}
.section {
    border-color: rgb(228, 228, 228);
}
.ig-list .ig-row a.ig-title, .str, .clo, .opn, .pun, .typ, .pln, .kwd, .tag, .atn, .atv, .dec, .com, .lit, .pages.show .page-title {
    color: rgb(228, 228, 228);
}
caption {
    background-color: #6d6d6d;
    color: rgb(228, 228, 228);
}
.alert {
    background-color: rgb(42, 42, 42);
    border: 2px;
    color: #c10e0e;
}
/* i really want this to be global */
}
