css:
@-moz-document domain("nytimes.com") {
     body {
         background-color: #222;
    }
     #app * {
         border-color: #444;
    }
     #app div, #app span, #app li {
         color: #CCC !important;
    }
     #app .assetWrapper p {
         color: #CCC !important;
    }
     #app .assetWrapper h2,#app h2, #app h3 {
         color: #EEE !important;
    }
     #app a {
         color: #EEE !important;
    }
     .balancedHeadline {
         color: #CCC !important;
    }
     [data-testid="masthead-mini-nav"] * {
         color:#EEE;
    }
     [data-testid="masthead-edition-menu"] * {
         color:#EEE !important;
    }
     svg {
         fill:white !important;
    }
     svg * {
         fill:white !important;
    }
     header > [aria-hidden="false"] {
         background: #222;
    }
     header > [aria-hidden="false"] a {
         color:#EEE !important;
    }
     [data-testid="user-settings-button"] > span {
         color:#EEE !important;
    }
     #desktop-sections-button {
         background: #222;
         color:#EEE !important;
    }
     div[class*="css"] {
         background: transparent !important;
    }
}
