css:
@-moz-document domain("cocoapods.org") {
body {
  background-color: #222;
  color: #eee;
}

#content-wrapper, #search_container, #results_container ol.results > li, #results_container ol.results ol.more {
  background-color: #444;
  color: #fff;
}

.pod_result, .pod-metadata, .sideline, .readme-content,
#results_container.active, #results_container.active .selection,
.dev-team .card,
.modal-content {
  background-color: #222;
}

pre, .readme-content pre, .readme-content > nav {
  background-color: #888;
  color: #fff;
  filter: brightness(0.8) contrast(1.5);
}

code {
  background-color: #634b4e;
}

.highlight,
.highlight .gp,
.highlight .nb,
.highlight .o,
.highlight .n,
.highlight .p,
.homepage-index li p {
  color: #fff;
}

#results_container ol.results > li.result em,
.integration, .download {
  background-color: #767676
}

#results_container ol.results > li.result .actions a {
  background-color: #746967;
}

[style*="background-color:white"] {
  background-color: #222 !important;
}

.intro {
  background-color: inherit;
}

.code-break, .code-break pre,
.info-row, .breadcrumb,
.popover{
    background-color: #333;
}
}
