css:
@-moz-document domain("twitch.tv") {
  .player-controls .seekbar-interaction-area, .player-controls .vod-seekbar-time-labels p[data-a-target="player-seekbar-duration"] {
      visibility: hidden;
  }
  .player-controls .player-controls__left-control-group {
      position: relative;
  }
  .player-controls .player-controls__left-control-group::before {
      content:'Video length and position hidden. Seek with arrow keys';
      position: absolute;
      top: -20px;
      color: white;
      padding-left: 9px;
  }
}
