css:
@-moz-document domain("community.adobe.com") {
.preview-card__body, .preview-card__external-link, .preview-card__subject--hover{
    display: none;
}
.preview-card__border-gap {
    display: none;
}
.ForumPage .preview-card__post-stats-strip {
    margin-top: 0px;
    margin-bottom: 0px;
}
.preview-card__subject{
    margin-top: 0px !important;
    font-size: 15px !important;
    line-height: 1.2 !important;
}
.preview-card__post-stats-strip--container {
    font-size: 3px !important;
    float: right;
    margin-top: -18px !important;
}
.preview-card__post-stats-strip--container:last {
    float: inherit;
}

.ForumPage .spectrum-Body5.preview-card__solution-timestamp-non-admin {
    margin-top: 18px !important;
}
.ForumPage .translate-button__text {
    max-width: 0.5em;
    overflow: hidden;
}

.ForumPage .featured-by-microsoft__message {
    display: none;
}
.preview-card__post-stats-color {
    z-index:1;
}
.community-page-reply-desktop-view{
    display: none !important;
}
.community-page-reply{
    display: none !important;
}
}
