css:
@-moz-document domain("szauna.sch.bme.hu") {


body {
    background: #222222;
}

div.column#content {
    background: #444444;
    color: #fff
}

input.input-class {
    background: #222222;
    color: #fff;
    border: #888 1px
}

.parent-row {
    background: #050;
}

.odd {
    background: #508;
}




.fancytable thead tr {
    color: #bb6aee;
}

#menu #menu-item-list li a:link,#menu #menu-item-list li a:visited {
     color: #bb6aee;
}


#footer a:link, #footer a:visited {
    color: #a4a9aC;
}

.carousel img {
       filter: brightness(0.7); 
}


}
