css:
@-moz-document url-prefix("https://crobi.github.io/dnd5e-quickref/preview/quickref.html") {

.page-background {
  background-color: #1b1b1b;
  /*csslint ignore: start*/
  overflow-y: overlay;
  /*csslint ignore: end*/
  padding-right: 17px; /*17 px - size of scrollbar, https://www.textfixer.com/tutorials/browser-scrollbar-width.php*/
}
.modal {
  overflow-y: hidden;
}
.modal-backdrop {
  background-color: #000;
}
.section-title {
  cursor: context-menu;
}
.section-content {
  background-color: #494949;
  text-shadow: 0 0 2px rgba(0, 0, 0, 0.5);
}
.item-icon {
  cursor: pointer;
}
}
