css:
@-moz-document domain("freefeed.net") {
    .create-post {
        border-bottom: none;
        margin-bottom: 0;
    }
    .post {
        padding-bottom: 20px;
        border-bottom: none;
    }
    .sidebar .logged-in {
        margin-bottom: 50px;
    }
    .box {
        margin-bottom: 28px;
    }
    .box-header, .box-header-empty, .box-header-freefeed, .box-header-friends, .box-header-groups, .box-header-memories, .box-header-timeline {
        border-bottom: none;
        font-weight: normal;
        font-size: 1.33em;
        margin-bottom: 0.33em;
    }
    .sidebar .box-body {
        padding: 0;
    }
}
