css:
@-moz-document domain("thatnovelcorner.com"), domain("jnovels.com") {
body, html,
.content .comments, div#disqus_thread
{
  background: #3d3d3f;
}

.hentry, .post,
.sidebar,
.comment-body,
.posts .page,

.comment-respond, .content div#respond
{
    background: #363639;
}

input, textarea,
.su-spoiler-style-fancy, .su-spoiler-style-fancy>.su-spoiler-title,
.posts .post-meta,
code, kbd, pre,
#totalpoll-id-76a970e19557b7ec2226b0a737139b07.totalpoll-poll-container .totalpoll-choices .totalpoll-choice .totalpoll-choice-container,
#sharing_email .sharing_send, .sd-content ul li .option a.share-ustom, .sd-content ul li a.sd-button, .sd-content ul li.advanced a.share-more, .sd-content ul li.preview-item div.option.option-smart-off a, .sd-social-icon .sd-content ul li a.sd-button, .sd-social-icon-text .sd-content ul li a.sd-button, .sd-social-official .sd-content>ul>li .digg_button>a, .sd-social-official .sd-content>ul>li>a.sd-button, .sd-social-text .sd-content ul li a.sd-button,
.post-header
{
    background: #2e2e2f !important;
}

#comment-form-comment
{
  background: none !important;
}

button
{
  color: #ccc;
  background-color: #0000;
}

body, input, textarea,
.post-header .post-title, .post-header .post-title a,
.post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6,
.comments-title, .comment-author .fn, .comment-author .fn a,
.su-spoiler-style-fancy,
.comment-reply-title
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.comment-body:after
{
  border-right-color: #363639;
}

.post .post-header:before, .page .post-header:before, .post .post-header:after, .page .post-header:after
{
  border-top-color: #2e2e2f;
}

}
