css:
@-moz-document domain(isnichwahr.de) {



/*	background	*/
body.html, .navbar .navbar-crumbs {
	background-color: #333;
}

/*	Liste	*/
.view-link-liste .linklist-table {
	background-color: #555;
}

.view-link-liste a,
.term-category-content span,
.term-category-content i {
	color: #ccc;
}

.view-link-liste > a:visited {
	color: #737373;
}

.today, .today a,
.today .term-category-content span,
.today .term-category-content i {
	color: #333;
}

/*	pagination	*/
.view-link-liste>.text-center  {
	background-color: #555;
}

.pagination > li > a,
.pagination > li > span {
    background-color: #737373;
    color: #efefef;
}

.pagination > li:hover > a,
.pagination > li:hover > span {
    
    color: #333;
}

/*	side content	*/
.user-profile-friend-item, .white-box  {
	background-color: #555;
}

/*	Comments	*/
form.comment-form, .comment {
	background-color: #555;
}

.comment p {
	color: #eee;
}

/*	img	*/
.node .content .image,
.node-picdump .field-name-field-picdump-image {
    background: #555;
}
.node-picdump .field-name-field-picdump-image .field-item {
	background: #737373;    
}
.node-picdump .field-name-field-picdump-image .field-item  a img {
    padding-top:10px;
}
}
