css:
/* i really want this to be global */
.ThemeSetter--silvered {
    background-repeat: no-repeat;
    background-size: cover;
    background-image: url(https://i.redd.it/4j12nz9jxj731.png);
    filter: brightness(0.5);
}
.BoardCard:hover {
    opacity: 1;
}
.BoardCard {
    background: black;
    color: white;
    opacity: 0.8;
}
.BoardCardWithCustomProperties-name {
    font-weight: 600;
    font-family: Open Sans;
}
.BoardCardWithCustomProperties--completed {
    opacity: 0.1;
    background: black;
}
.CardButton {
    background: black;
}
.Board-pageToolbar {
    background: black;
    opacity: .5;
}
.AsanaPageTopbar:hover {
    opacity: 1;
}
.AsanaPageTopbar {
    background: black;
    opacity: 0.95;
}
.AsanaPageTopbar:hover {
    opacity: 1;
}
.TopbarPageHeaderGlobalActions {
    background: black;
}
.TopbarPageHeaderGlobalActions:hover {
    opacity: 1;
}
.TopbarSearchTypeahead-background {
    background:black;
}
.TopbarSearchTypeahead-background::before {
    display: hidden;
}
.BoardBody-columnDraggableItemWrapper:nth-of-type(2) {

}
.BoardColumnWithSortableTasks-sortableList.is-empty::before {
    opacity: .2;
}
.TopbarSearchTypeahead--withSpreadsheetGrid .TopbarSearchTypeahead-background::before {
    display: none;
}
.TopbarPageHeaderStructure-rightChildrenWrapper::after {
    border: none;
}

.BoardCard--completed {
    background: #180a33f0;
    opacity: 0.6;
}

.SortableList-sortableItemContainer:first-of-type {
    margin-right: 100px;
}
