css:
@-moz-document domain("web.whatsapp.com") {
  img {
      filter: blur(5px);
  }

  img:hover {
      filter: blur(0);
  }

  span[dir="ltr"], span[dir="rtl"], span[dir="auto"] {
    filter: blur(3px);
  }

  span[dir="ltr"]:hover, span[dir="rtl"]:hover, span[dir="auto"]:hover {
    filter: blur(0);
  }

  div.message-in span, div.message-out span, div.message-out ~ div span, div.message-in ~ div span {
    filter: blur(0);
  }
}
