css:
@-moz-document domain("wnacg.com"), domain("wnacg.org"), domain("wnacg.net")
{
#tuzaoblock,
.footer,
.comment_wrap,
.grid .sel_on .pic_ctl,
#bread>a {
	display: none !important;
}

* {
	font-family: sans-serif;
}

html,
body {
	height: auto;
	min-height: 100%;
}

body {
	max-width: 2000px;
	padding: 5mm 0 50px;
	margin: 0 auto;
	background: linear-gradient(77deg,
			rgba(142, 140, 155, .7) 0,
			rgba(205, 200, 185, .8) 40%,
			rgba(205, 200, 185, .8) 60%,
			rgba(142, 140, 155, .7) 100%)
}

body,
.nav li a {
	font-size: 10pt;
}

img {
	object-fit: contain
}

.header,
#bread,
#bodywrap {
	min-width: 680px;
	max-width: calc(100vw - 16mm);
}

.search input {
	font: 12pt sans-serif;
	line-height: 1.5;
	margin: 4px 0 0 10px;
}

#photo_body {
	text-align: center;
}

#imgarea img {
	display: inline-block;
	max-width: 98vw;
	max-height: 88vh;
	border-radius: 2mm;
	box-shadow: 0 2px 3mm #555;
}

#img_list img {
	width: auto;
	max-width: 98vw;
	height: calc(100vh - 21.5mm);
	border-radius: 2mm;
	background-color: rgba(0, 0, 0, .5);
	box-shadow: 0 2px 3mm #555;
}

#img_list>div {
	background: transparent;
}

#img_list span {
	color: #eee !important;
}

.userwrap {
	border: none;
	margin: 0;
}

.userwrap .asTB {
	width: 100%;
	height: auto;
	display: inline-block;
	background: #ced0d8;
	max-height: 220px;
	overflow: hidden;
	border-radius: 2mm;
	box-shadow: 0 0 2mm #666;
	transition: .5s;
}

.userwrap .asTB:hover {
	max-height: 80vh;
	overflow: scroll;
	transition: .5s .3s;
}

.userwrap .asTB>* {
	padding: 8px
}

.uwthumb {
	display: block;
	float: left;
	width: 300px;
	padding: 0;
}

.uwthumb img {
	float: left;
	margin: 0;
	width: auto;
	max-width: 160px;
	height: 200px;
}

.uwconn {
	display: block;
	float: left;
	width: calc(100% - 628px) !important
}

.uwuinfo {
	display: block;
	float: right;
	width: 280px;
	height: 100%;
}

.selectlist {
	border-radius: 5mm;
}

.tagshow,
.btnNarrow,
.btntuzao {
	display: inline-block;
	line-height: 1.8;
	padding: 0 2.5mm;
	border-radius: 1em;
}

#bread {
	padding: .2em 0;
	margin-bottom: 3px;
	line-height: 1.5em;
}

#bread .bread {
	background-position: 0 0;
}

.selectlist li:first-child a {
	padding-left: 1em;
}

.selectlist li:last-child a {
	padding-right: 1em;
}

.grid ul {
	font-size: 0;
}

.grid li {
	font-size: 11pt
}

.gallary_wrap li.li {
	vertical-align: top;
	display: inline-block;
	height: auto;
	padding: 3mm 3mm 0;
	margin: 0 0 2mm;
	border-radius: 2mm;
	float: none;
	background: transparent;
	transition: .2s;
}

.gallary_wrap li.li:hover {
	box-shadow: 0 0 2mm #666;
	background: rgba(255, 255, 255, .3);
	transition: .1s;
}

.grid .pic_box img {
	width: 100%;
	object-fit: cover;
	padding: 0;
	margin: 0 auto 1mm;
	border-radius: 1mm;
	box-shadow: 0 0 0 1mm #eee;
	transition: .2s;
}

.grid .pic_box {
	margin: 0;
}

.grid .info {
	margin-top: 1mm
}

.grid .info .title {
	width: auto;
}

.grid .info .title a {
	width: auto;
	max-height: 6em;
	color: #333;
	display: -webkit-box;
	-webkit-line-clamp: 2;
	-webkit-box-orient: vertical;
	overflow: hidden;
}

.grid .info .title a:hover {
	text-decoration: none;
	color: #626;
}

.grid .info .info_col {
	text-align: right;
	color: #447;
	font-size: 10pt;
	line-height: 1.4em;
	padding: 3px 0 2mm;
}

.drop_dwon {
	border-radius: 0 0 2mm 2mm;
	overflow: hidden;
	box-shadow: 0 0 5px #888;
}

#header {
	position: fixed;
	width: 100vw;
	padding: 0;
	top: -7mm;
	z-index: 2;
	box-shadow: 0 0 0 1px #ccc, 0 0 2mm #000;
	opacity: .4;
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	transition: .8s;
}

#header:hover {
	opacity: 1;
	top: 0;
	transition: .1s;
}

.newpagewrap,
.bot_toolbar {
	position: fixed;
	left: 0;
	bottom: 0;
	width: 100vw;
	padding: 0;
	background: rgba(0, 0, 0, .5);
	text-align: center;
	box-shadow: 0 0 0 1px #ccc, 0 0 2mm #000;
	opacity: .4;
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	transition: opacity .8s;
}

.newpagewrap:hover,
.bot_toolbar:hover {
	opacity: 1;
	transition: opacity .2s;
}

.newpageblock,
.paginator.f_left {
	float: none;
	display: inline-block;
	min-width: 700px;
	margin: .7em auto;
}

.paginator .thispage,
.paginator a {
	min-width: 1.5em;
	border-radius: 2mm;
	margin-right: 1mm;
	padding: 0 .5em;
	display: inline-block;
	float: none;
}

.paginator .prev {
	float: left;
}

.paginator .next {
	float: right;
}

.paginator a:hover {
	padding: 0 .5em;
}

.newpagelabel {
	position: absolute;
	right: -100px;
	font-size: 18pt;
	color: #fff;
}

.newpage {
	position: relative;
	left: 0;
	top: 0;
}

#photo_infos {
	margin: 0;
}

.adbox {
	width: 50vw;
	min-width: 400px;
	max-width: 600px;
	height: 260px;
	margin: 30vh auto 10mm;
	background: rgba(240, 240, 240, .7);
	box-shadow: 0 2mm 6mm rgba(0, 0, 0, .7);
	border-radius: 3mm;
	backdrop-filter: blur(3mm) saturate(.6);
	-webkit-backdrop-filter: blur(3mm) saturate(.6);
}

.btn {
	text-align: center;
	width: auto !important;
	min-width: 5em;
	height: auto;
	padding: .2em .5em;
	font: normal 13pt sans-serif;
	border-radius: 2mm;
	box-shadow: none;
}

.btn:hover {
	background: linear-gradient(180deg, #7c7 0%, #6b6 100%) !important;
	box-shadow: 0 0 1mm #aaa;
}

.btn:active {
	background: linear-gradient(180deg, #5a5 0, #494 100%) !important;
	box-shadow: inset 0 0 1mm #777;
}

.download_panel,
.download_btn {
	text-align: center;
	width: auto;
	height: auto;
	padding: 1em 2em;
	margin: 0;
	background: transparent;
	box-shadow: none;
}

.download_filename {
	font: normal 13pt sans-serif;
	white-space: normal;
	max-height: 3em;
	word-break: break-word;
	overflow: visible;
	padding: 0;
}

.down_btn {
	display: inline-block !important;
	float: none;
	width: 5.5em;
	height: auto;
	font: normal 13pt sans-serif;
	line-height: 1.5;
	padding: .3em .7em;
	margin: 0 5% 3mm;
	border-radius: 2em;
	background-color: #268;
}

.down_btn:hover {
	color: #df6;
	background-color: #37a !important;
	box-shadow: 0 1px 4px rgba(0, 0, 0, .8), inset 0 0 0 1px #ccc;
}

.down_btn:active {
	box-shadow: inset 0 1px 4px rgba(0, 0, 0, .8), 0 0 0 1px #eee;
}

@media screen and (max-width:1000px) {
	.gallary_wrap li.li {
		width: calc(100%/3 - 6mm);
	}

	.grid .pic_box img {
		height: 20vh;
	}

	.grid .info .title a {
		font-size: 10pt;
		-webkit-line-clamp: 2;
	}

}

@media screen and (min-width:1000px) {
	.gallary_wrap li.li {
		width: calc(100%/4 - 6mm);
	}

	.grid .pic_box img {
		height: 24vh;
	}

	.grid .info .title a {
		font-size: 11pt;
		-webkit-line-clamp: 2;
	}

}

@media screen and (min-width:1300px) {
	.gallary_wrap li.li {
		width: calc(100%/5 - 6mm);
	}

	.grid .pic_box img {
		height: 27vh;
	}
	.grid .info .title a {
		font-size: 12pt;
		-webkit-line-clamp: 2;
	}
}

@media screen and (min-width:1600px) {
	.gallary_wrap li.li {
		width: calc(100%/6 - 6mm);
	}

	.grid .pic_box img {
		height: calc(50vh - 270px);
	}
	.grid .info .title a {
		font-size: 13pt;
		-webkit-line-clamp: 3;
	}
}
}
