css:
@-moz-document domain("ntv.com.tr") {
body.main #story div.content aside {
        display: none !important;
    }
    body.main #story div.content article {
        width: 100% !important;
    }
    .wrapper infinite story-wrapper dygad-pointer-content-top ng-scope {
        display: none !important;
    }
    body.main .sticky-wrapper {
        display: none !important;
    }
    body.main .weather {
        display: none !important;
    }
    body.main .content_wrapper > header .icon-menu {
        display: none !important;
    }
    body.main .finance-widget {
        float: right !important;
    }
    html {
        font-family: "Helvetica Neue, Segoe UI, Ubuntu Regular, sans-serif" !important;
    }
}
