css:
@-moz-document url-prefix(https://hackforums.net/), url-prefix(http://hackforums.net/){
  html > body{
   background: #111 url(https://i.imgur.com/56elO3F.png)!important;
   background-size: contain!important;
  }

  /* default background */
  table .thead, .ghead{
   background: #282828!important;
  }
  /* highlight color */
  .shadetabs li a.selected,
  .quickthread_button,
  .pagination .pagination_current{
   background: #484848!important;
  }

  #logo img.logo-img{
   filter: sepia(1) brightness(1.25) hue-rotate(160deg);
  }
}
