css:
@-moz-document url-prefix("https://irath96.github.io/webNBT/") {
* {
    background-color: #3d3d3d;
    color: white
}

pre#hexview, div#topbar {
    border-color : #020202;
    background-color: #6f6c6c
}

.jstree-default .jstree-clicked {
    background: #4f4d4ddb
}

.vakata-context li>a {
    color: white;
    text-shadow: none
}

.vakata-context li>a:hover, .vakata-context .vakata-context-hover>a, .jstree-default .jstree-hovered {  
    background: #6a6868db
}

b, i {
    background-color: transparent
}
}
