css:
@-moz-document domain("platforma.polsl.pl"), domain("cze.polsl.pl") {
/*** Main page ***/
body, html, div, table  {
    background: #252525!important;
    color: #e8e8e8!important;
}
.footerInner  {
	background: #1b1b1b!important;
	border-color: #333!important;
}

.fixed-top ,.navbar ,.navbar-light ,.bg-white ,.navbar-expand ,.moodle-has-zindex, .page-link{
    background: #171717!important;
    color: #e8e8e8!important;
}

.nav-link, section, .card-body, .has-blocks, .mb-3{
    background: #252525!important;
    color: #e8e8e8!important;
}
.site-name{
    color: #e8e8e8!important;
}


.dropdown-menu, .dropdown-item, .show, .list-group, .mt-1, .list-group-item, .list-group-item-action{
    background: #252525!important;
    color: #e8e8e8!important;
}
.dropdown-item:hover{
    background: #171717!important;
}

textarea, input, select{
    background: #515151!important;
    color: #e8e8e8!important;
}
}
