css:
@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@namespace svg url(http://www.w3.org/2000/svg);

@-moz-document url-prefix("https://mailchi.mp/expresso/") {
* {
    color: #afafaf;
}

body,
table,
td {
    background: #1F1F1F !important;
    background-color: #1F1F1F !important;
}

.mcnTextContent a {
    text-decoration-color: #d2d2d2 !important;
}

.mcnTextContent a span {
    color: #d2d2d2 !important;
}
a {
    color: rgb(79, 140, 201) !important;
}
.mcnDividerContent {
    border-top-color: rgb(79, 140, 201) !important;
}

.mcnBoxedTextContentContainer table {
    border-color: #404040;
}
.mcnBoxedTextContentContainer strong {
    color: #d2d2d2 !important;
}
.mcnBoxedTextContentContainer .mcnTextContent {
    border: 1px solid #404040;
    background-color: rgb(24, 24, 24) !important;
}
.mcnTextBlockOuter .mcnTextContent {
    color: #949494 !important;
}
.mcnTextBlockOuter .mcnTextContent a,
.mcnTextContent u a {
    text-decoration: none !important;
}
}
