css:
/* i really want this to be global */

@-moz-document url-prefix("https://www.diariodeleon.es/")
{

body, .content-info.site-color-b  {
    background-color: #20202C !important;
    color: white;
}

.article-navbar-contents, .row.footer-data {
    background-color: #0C0C10;
}

a, h1, h2, h3, h4, div, span, p, figcaption {
    color: white !important;
}

.textarea, .input-wrapper>input {
    background: white !important;
    color: #981d56;
}


/* Logo */
h1.header-logo, .footer-logo {
    filter: brightness(0) invert(100%);
}

.content-utilities.statics {
    display: none !important;
}

.metadata.tags>a {
    background: #981d56;
}

.metadata.tags>a:hover {
    color: #981d56!important;
}

.big-content>.article-data>.content-info.site-color-b {
    background-color: #981d5680 !important;
    padding-left: 2rem;
}

.owl-controls>div>div>i {
    color: white !important;
}

.owl-controls {
    border-top: 1px solid rgba(255, 255, 255, .1) !important;
}

.owl-dot {
    background: white !important;
}

.widget-content-listing .widget-content .owl-carousel.owl-theme {
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, .15)
}

}
