css:
@-moz-document domain("chat.iconnectguam.com") {
.rc-old .page-container, .rc-old .page-container .content {
    background-color: #1e272e;
    color: white !important;
}

.rc-header, .rc-header__wrap, .rc-header__data, .wrapper, .message.new-day:after, .message.new-day:before {
    background-color: #1e272e;
    color: white !important;
}

.color-primary-font-color , .message {
    background-color:  #1e272e !important;
    color: white !important;
}

.rc-popover__content, .rc-popover__content * {
    background-color: #1e272e;
    color: white !important;
}

.contextual-bar, .contextual-bar__header {
    background-color: #1e272e;
    color: white !important;
}

input, select, textarea, .rc-member-list__counter, .thread-list, .contextual-bar__content {
    background-color: #1e272e;
    color: white !important;
}

.footer, .rc-message-box, .rc-message-box textarea {
    background-color: #1e272e;
    color: white !important;
}

.emoji-picker {
    background-color: #1e272e;
    color: white !important;
}

.role-tag {
    background-color: #d63031 !important;
    color: white !important;
    border: none;
}

.rc-message-box__typing-user {
    color: white !important;
}
}
