@-moz-document url(https://discordapp.com) {
	body {
		background-color: black !important;
	}
}


/* i really want this to be global */

*{box-sizing:border-box;}
            .uploadOuter {margin: 6px 0;min-height:30px;border:1px solid #ccc;position: relative;width: 310px;}
            .uploadFile {opacity: 0;position: absolute;right: 0;top: 0;z-index: 2;height:100%; width: 100%;}
            .uploadText{width:100%;height:100%;position:absolute;border:none;padding:0 0 0 10px;background: url('https://wallpaperplay.com/walls/full/5/1/b/91780.jpg') no-repeat right center}
            .Preview{width:200px;border:1px solid #ccc;position:relative;display:none}
            .Preview img{width:100%;}
            .loader{position:absolute;left:0;top:0;width:100%;height:100%;background:rgba(0, 0, 0, 0.5) url('https://wallpaperplay.com/walls/full/5/1/b/91780.jpg') no-repeat center center;z-index:99; }
