/* ==UserStyle==
@name        Mailgun Dark
@namespace   XDelta
@version     1.0.0
@homepageURL https://github.com/XDelta/StylusUserCSS
@updateURL   https://raw.githubusercontent.com/XDelta/StylusUserCSS/master/mailgun-dark.user.css
@license     CC-BY-SA-4.0
@author      XDelta
==/UserStyle== */
@-moz-document domain("app.mailgun.com") {

:root {
	--primary: /*[[primary]]*/;
	--primary25: /*[[primary25]]*/;
	--text: /*[[text]]*/;
	--bg1: /*[[bg1]]*/;
	--bg2: /*[[bg2]]*/;
    	--bg3: /*[[bg3]]*/;
}

body {
    color: var(--text);
    background-color: var(--bg1);
}
h1, h2, h3, h4, h5, .mg-link, .Box-header, .UnderlineNav-item.selected, .text-blue-dark {
    color: var(--text) !important;
}

/*Theme Color*/
.mg-sidebar, .bg-gradient-blue-dark {
    background-image: linear-gradient(to right, var(--primary25), var(--bg1));
    background-color: var(--bg1);
}
.mg-header, .selected {
    border-bottom: 2px solid var(--primary) !important;
}
.svg-icon--color-blue-dark {
    fill: var(--primary);
}
.overview-link {
    color: var(--primary) !important;
}
/*End Theme Color*/

.mg-content, .subnav-item {
    background-color: #050505 !important;
    border: none;
}
.bg-white, .Box, .card-details-row .plan-card .plan-details .plan-summary {
    background-color: var(--bg1) !important;
}
.Box-row {
    border-bottom: 1px solid var(--bg2);
}
.Box-header, .UnderlineNav, .bg-gray-light, thead {
    background-color: #1e1e1e !important;
}
.mg-link.mg-link-background--green, .mg-link.mg-link-background, .jss32 {
    background-color: #050505;
}
.input-group .form-control, .form-group .form-control, .search, .css-13gnbqd-control, .css-1jn0d25 {
    background-color: #777;
    border: none;
}

/*Buttons*/
.btn-primary {
    background-color: #438869 !important;
    background-image: none;
}
.btn-danger {
    color: #c02428;
    background-color: #c0242839 !important;
    background-image: none;
}
.btn-upgrade {
    background-color: #555 !important;
}
.btn-upgrade:disabled {
    background-image: none;
}
::placeholder {
  color: var(--bg3);
  opacity: 1;
}
.text-green {
    color: #28a745 !important;
    background-color: #0000;
}
/*End Buttons*/

.mg-sidebar .mg-sidebar-child--active, .mg-sidebar .mg-sidebar-child:hover {
    background-color: #050505;
}
.mg-sidebar .child-container {
    background-color: #040404aa;
}

}
