css:
@-moz-document domain(hackerrank.com) {
* {
    box-shadow: none !important;
}

.community-header-breadcrumb {
    background-color: #333;
    color: #ddd;
    border-bottom: 1px solid #ddd;
   
}

.contest-tab-expander {
    background-color: #333;
    color: #ddd;
    border-color: #888;
    
}

#hr_v2 .main_content .contests .active_contests > ul > li {
      border-color: #555;
}

.body-wrap {
    background-color: #222;
}

 .dashboard-page,
.new-challenge-list,
.ui-tabs-wrap .tab-list-content,

.theme-m .hackdown-container li, .theme-m .hackdown-container p
{
    background-color: #333;
    color: #ddd;
}

strong, label, .txt-navy {
    color: #eee !important;
}

.theme-m-content.submission-status-banner{
    background:#394a6d;
}

.ui-card ,
.community-footer,
.theme-m .new-challenge-list .no-challenge-list,
.content--list_body
{
    background-color: #444;
    color: #eee;
}

h1,h2,h3,h4,h5,h6,
.theme-m .text-sec-headline-s, 
.theme-m .text-sec-headline-xs,
.base-card-title,
.skills-section .track-item,
.theme-m .community-footer .footer-link,
.theme-m .new-challenge-list .challengecard-title

{
    color: #16ef7d;
}

a {
    color: #16ef7d;
    text-decoration: underline;
}

g {
    fill: #16ef7d !important;
}

.theme-m .hackdown-container p code {
    color: white;
    background-color: #222;
    padding: 4px;
    border: 1px solid #555;
}

pre {
    background-color: #ddd !important;
}
}
