css:
@-moz-document domain("cmu.ca") {
body {
    background: black !important;
}
a,
span,
td,
.dropdownMenuItem,
.inactiveMenuItem {
    color: #9a9a9a !important;
}
.TableSubDetail {
    background: black !important;
    border: 2px solid #232222;
}
button,
input,
#divHeadingMenuRow1 {
    color: #9a9a9a;
    background: black !important;
    border: 1px solid #232222 !important;
}
#headingColor,
#heading1,
#heading2,
#heading3,
#headingPad,
#tdNavBar {
    background: #061c11;
}
#mnuHomeId {
    color: #9a9a9a !important;
}
.RoundedCollapsibleContent {
    background: black !important;
    border-left: 1px solid #232222 !important;
    border-right: 1px solid #232222 !important;
    border-bottom: 1px solid #232222 !important
}
.dropdownMenu {
    background: black !important;
    border: 1px solid #232222 !important;
    box-shadow: none;
}
.RoundedCollapsiblePanel {
    background: #061c11 !important;
    box-shadow: none !important;
    border-left: 1px solid #232222 !important;
    border-right: 1px solid #232222 !important;
    border-top: 1px solid #232222 !important;
}
.HeaderButton {
    background: #072114!important;
}
div.loadingMsg {
    background: black !important;
}
#loadingMsgText {
    background: black !important;
    border: 2px solid #232222 !important;
}
.TableDetailCheck {
    background: black !important;
}
}
