css:
@-moz-document domain(www.ecosia.org) {

.row {
  	background-color: light-grey;
}

.search-header {
 	background-color: light-grey;   
}

.product-ad {
    display: none;
}

.result-url {
 	color: #006621;
    font-size: 16px;
}

.result-snippet {
 	font-size: 14px;
    padding-top: 5px;
    line-height: 1.57;
    color: #545454
}

.result-firstline-container {
 	margin-top: 20px;   
}

.result-title {
 	color: #1A0DAB;
    font-size: 20px;
}

.result-deeplink-item {
  	overflow: hidden;
  	text-overflow: ellipsis;
}

.result-deeplink-item .result-title {
    font-size: 18px;
}

.card-desktop + .card-web {
    margin-top: 0;
}

.result-count {
 	margin-bottom: 0;   
}

.ad-hint {
 	font-size: 17px;
    color: #009933;
}

.card-ad {
 	display: none;
}

.js-sidebar-ad {
 	display: none;   
}

body {
 	font-family: Arial;
}

}
