css:
@-moz-document domain("www.ecosia.org") {
* {
    scrollbar-color: #3F3F3F #1C1E1F;
    --main-background-color: #181A1B;
    --form-color: #1F1F1F;
    --border-color: #595959;
    --text-color: #CCCCCC;
    --link-color: #6E9BF4;
    --link-visited-color: #CF70FF;
    --link-green-color: #71D89A;
    --darker-background-color: #3F3F3F;
}

/* Banner */
.content[target="_blank"] {
    background: transparent !important;
}

/* --- */
/* SVG */
/* --- */
.background[data-v-9cc6f52e] > path, .background[data-v-c06d85e8] path {
    fill: #404040;
}

.pagination-button > svg > g > g, .video-slider-controls button > svg > g > g, .logo-anchor path:nth-child(2), .logo path:nth-child(2), .icon-button[data-track-id="burger-menu"]:hover > svg > path, .icon-button[data-track-id="burger-menu"]:focus > svg > path {
    fill: white;
}

.pagination-button:hover > svg > g > g {
    transition: fill 0.2s ease-in-out;
    fill: var(--border-color);
}

.is-open .pill svg > path, .pill:hover svg > path {
    fill: var(--text-color) !important;
}

.land-background > svg > path {
    fill: var(--darker-background-color);
}

.svg-icon .background {
	fill: var(--main-background-color);
}

/* Side results */
.entity-title {
    color: var(--text-color);
}

[aria-label="sources"] {
    background: var(--form-color) !important;
}

/* ------- */
/* WIDGETS */
/* ------- */

/* Maths widget */
.card-computation {
    background: var(--form-color) !important;
    border: 1px solid var(--border-color);
    box-shadow: 0 0 3px 0 rgba(255, 255, 255, 0.2), 0 0 3px 0 rgba(255, 255, 255, 0.2);
}

.widget {
    background: var(--main-background-color);
}

.widget__footer-item {
    border-top: 1px solid var(--border-color);
}

/* Flights widget */
.widget-flights {
    background: var(--form-color);
    box-shadow: 0 0 3px 0 rgba(255, 255, 255, 0.2), 0 0 3px 0 rgba(255, 255, 255, 0.2);
}

.widget-flights__footer {
    border-top: 1px solid rgb(59, 59, 59);
}

.widget-flights, .SearchWidget__searchWidgetFieldsWrap___6HsSR {
    background: var(--form-color) !important;
}

/* Corona widget */
.callout--corona {
    background: var(--form-color);
    box-shadow: 0 0 3px 0 rgba(255, 255, 255, 0.2), 0 0 3px 0 rgba(255, 255, 255, 0.2);
}

.callout__header, .callout__nav-item:not(:last-of-type) {
    border-bottom: 1px solid var(--border-color);
}

.callout__nav {
    background: var(--form-color) !important;
}

.callout__nav-link {
    color: var(--link-color);
}

.callout__nav-link:hover, .callout__nav-link:focus, .callout__nav-link:active {
    color: var(--link-color) !important;
}

.callout__nav-link:visited {
    color: var(--link-visited-color) !important;
}

.callout__nav-description, .callout__heading {
    color: var(--text-color);
}

/* Polluter pop-up */
.popover-result {
    color: var(--text-color);
    box-shadow: 0 0 2px 0 rgba(255, 255, 255, 0.3), 0 0 2px 0 rgba(255, 255, 255, 0.3);
}

.popover-arrow {
    background: var(--form-color);
}

/* Notification banner */
.notifications-banner {
    background: var(--form-color);
}

.notifications-banner-content {
    color: var(--text-color) !important;
}

/* ------------ */
/* MAIN BUTTONS */
/* ------------ */

/* Pill buttons */
.pill {
    background: var(--main-background-color) !important;
    border: 0 !important;
    color: var(--text-color) !important;
    box-shadow: 0 0 2px 0 rgba(255, 255, 255, 0.3), 0 0 2px 0 rgba(255, 255, 255, 0.3) !important;
    transition: background-color 0.2s ease-in-out;
}

.pill:hover, .icon-button[data-track-id="burger-menu"]:focus, .icon-button[data-track-id="burger-menu"]:hover {
    background: var(--border-color) !important;
    color: var(--text-color);
}

.is-open .pill, .pill.is-open {
    background: var(--darker-background-color) !important;
    color: var(--text-color) !important;
}

[data-track-id="personal-counter"] > .text {
    color: var(--text-color);
}

.icon-button[data-track-id="burger-menu"]:hover {
    background: var()
}

/* Pagination */
.pagination, .pagination-next, .pagination-prev {
    background: var(--main-background-color) !important;
}

.pagination-button.active {
    color: white;
}

.pagination-next.enabled:hover {
    background-image: none;
}

.pagination-button:hover {
    background-image: none;
    color: var(--text-color);
}

.pagination-button, .pagination-button.active {
    background-image: none;
}

[aria-controls="tns1"] {
    background: var(--form-color) !important;
    box-shadow: 0 0 2px 0 rgba(255, 255, 255, 0.15), 0 0 2px 0 rgba(255, 255, 255, 0.15) !important;
    transition: opacity .24s ease-out,box-shadow .24s ease,-webkit-box-shadow .24s ease;
}

[data-controls="next"]:hover, [data-controls="prev"]:hover {
    background: var(--border-color) !important;
    box-shadow: 0 0 7px 0 rgba(255, 255, 255, 0.15), 0 0 7px 0 rgba(255, 255, 255, 0.15) !important;
}

[data-controls="next"]:hover > svg > g > circle, [data-controls="prev"]:hover > svg > g > circle {
	fill: var(--border-color);
}

/* ---------------- */
/* POPUPS IN NAVBAR */
/* ---------------- */
[data-track-id="menu"] {
    color: white !important;
}

.dropdown-item:hover, [data-update="mc"] > div {
    background: var(--form-color) !important;
}

.dropdown-menu-group {
    border-bottom: 1px solid var(--border-color);
    background: var(--form-color) !important;
}

.dropdown-link:hover {
    color: #E6E6E6;
    background: #4D4D4D;
    border-bottom: 1px solid var(--border-color)
}

/* Hamburger popup */
.icon-button[data-track-id="burger-menu"] {
    border: 1px solid var(--border-color) !important;
}

.group {
    border-bottom: 1px solid var(--border-color) !important;
}

/* Notifications popup */
.more {
    border-top: 1px solid var(--border-color) !important;
}

/* Tree-counter popup */
.personal-counter__dropdown {
    background: var(--form-color) !important;
}

.personal-counter__text {
    color: var(--text-color) !important;
}

.popover-footer {
    border-top: 1px solid var(--border-color);
}

.link-primary[data-v-3cdf64d9]:hover, .learn-more[data-v-3cdf64d9]:hover {
    background: var(--form-color) !important;
}

#treeCountPopover {
    box-shadow: 0 0 3px 0 rgba(255, 255, 255, 0.15), 0 0 3px 0 rgba(255, 255, 255, 0.15);
    border-top: none;
}

.popover {
    background-color: var(--form-color);
}

.popover-learnmore {
    border: 1px solid var(--border-color);
}

.popover-section, .popover-learnmore-footer {
    background: var(--form-color) !important;
    border-top: 1px solid var(--border-color) !important;
}

.flags-dropdown-more {
    border-top: 1px solid var(--border-color);
    background: var(--form-color);
}

.flags-dropdown-list > li:hover {
    background: var(--darker-background-color);
}

.flags-dropdown-chosen {
    background: transparent url(https://i.ibb.co/FY3RvQS/PNG-Image-16-16-pixels.png) no-repeat 268px 16px;
    border-bottom: 1px solid var(--border-color);
}

.flags-dropdown-header {
    border-bottom: 1px solid var(--border-color);
}

.filter-trigger {
    background: var(--darker-background-color);
    color: var(--text-color);
    border: 1px solid var(--border-color);
}

.filter-trigger:hover {
    color: var(--text-color);
    background: var(--border-color);
}

.filter-list {
    background: var(--darker-background-color);
    color: var(--text-color);
    box-shadow: 0 2px 8px 0 rgba(255, 255, 255, 0.15), 0 1px 4px 0 rgba(255, 255, 255, 0.15);
}

.arrow-down > g > g {
    fill: var(--text-color);
}

.filter-link {
    color: var(--text-color);
    background: var(--darker-background-color);
}

.filter-link:hover, .filter-link:focus {
    background: var(--form-color);
    color: var(--text-color);
}

.filter-list > .active > .filter-link {
    background: var(--border-color) !important;
}

.dropdown-menu {
    box-shadow: 0 0 4px 0 rgba(255, 255, 255, 0.15), 0 0 4px 0 rgba(255, 255, 255, 0.15);
}

.icons-dropdown-list {
    background: var(--main-background-color);
}

.icons-dropdown-link, .icons-dropdown-link:visited {
    color: var(--text-color) !important;
}

.icons-dropdown-list > li:hover {
    background: var(--border-color) !important;
}

.notifications-center-container {
    border: 1px solid #5F5F5F;
    box-shadow: 0 0 3px 0 rgba(255, 255, 255, 0.15), 0 0 3px 0 rgba(255, 255, 255, 0.15);
}

.notifications-item, .notifications-item:active, .notifications-item:focus, .notifications-item:visited {
    background: var(--form-color);
    border-bottom: 1px solid #5F5F5F !important;
}

.notifications-item:hover {
    background: #4D4D4D;
}

.notifications-center-more {
    background: var(--form-color);
}

.dropdown-footer-simple, .dropdown-footer-double {
    background: var(--main-background-color);
    border-top: 1px solid var(--border-color)
}

/* Popups */

.main-popup, .followup-popup, .followup-popup.fade-out {
    background: var(--main-background-color) !important;
    border: 2px solid #595959;
    color: var(--text-color) !important;
}

/* Navbar search types */
.navbar-row {
    background-color: var(--main-background-color);
    background-image: none;
    border-bottom: 1px solid #373737;
}

.navbar-link {
    color: #CAC6BE;
}

.navbar-link:visited {
    color: var(--text-color);
}

/* ------------ */
/* MAIN RESULTS */
/* ------------ */
.result-title, .result-title:hover, .result-title:focus, .result-title:active, .thumbnail-title, .more-results-link {
    color: var(--link-color) !important;
}

.results-page {
    background: var(--main-background-color);
}

.result-title:visited, .card-mobile > a:visited {
    color: var(--link-visited-color) !important;
}

.result-url, .result-url:hover, .result-url:focus, .result-url:active, .result-url:visited, .thumbnail-publisher, .news-publisher, .video-publisher {
    color: var(--link-green-color);
}

.thumbnail-item {
    background: #292929;
    border: 1px solid var(--border-color);
}

time {
    color: #898989 !important;
}

.card-desktop + .card-desktop {
    margin-top: 20px;
}

.callout {
    background-color: var(--form-color);
    border: 1px solid var(--border-color);
    box-shadow: 0 0 5px 0 rgba(255, 255, 255, 0.2), 0 0 5px 0 rgba(255, 255, 255, 0.2) !important;
}

.callout-title {
    color: white;
}

/* Results footer */
.after-results-info {
    border-top: 1px solid var(--border-color);
}

/* ---------- */
/* SEARCH BAR */
/* ---------- */

/* Search bar */
.search-form-field {
    background: transparent;
    border: 1px solid var(--border-color);
}

form[action="/search"] {
    box-shadow: none !important;
}

.search-form {
    border: 1px solid #5F5F5F !important;
}

.search-form-input {
    background: transparent !important;
}

/* Search prediction (typeahead) */
.typeahead-link {
    background: var(--form-color);
    color: white;
}

.typeahead-link:hover {
    background: #2F2F2F;
    color: white;
}

.typeahead-link:visited {
    background: var(--form-color);
    color: #AAAAAA;
}

.typeahead-suggestion, .typeahead-suggestion:not(:last-of-type) {
    border-bottom: 1px solid var(--border-color);
}

.typeahead {
    border: 1px solid var(--border-color);
	background: var(--border-color);
}

/* ------ */
/* MOBILE */
/* ------ */
.hidden-desktop .flags-dropdown-list, .flags-dropdown-header {
    color: var(--text-color);
}

.hidden-desktop .icons-dropdown-list {
    border-bottom: 1px solid var(--border-color);
    border-left: 1px solid var(--border-color);
    border-right: 1px solid var(--border-color);
}

.navbar-container {
    background: var(--main-background-color);
}

.navbar-item-button {
    border-left: 3px solid var(--main-background-color);
}

.search-form-addon-button {
    background: var(--form-color);
    border-left: 3px solid var(--form-color);
}

.card-mobile {
    background-color: transparent;
}

.offcanvas-menu-header {
    background: var(--form-color);
}

.filter-select {
    background: #404040;
    color: var(--text-color);
}

.panel[data-v-63c7948a] {
    background: #333333;
    border-left: 1px solid var(--border-color);
}

.panel-header[data-v-3d767220] {
    background: #333333;
}

.group[data-v-5ee46ea2] {
    border-bottom: 1px solid var(--border-color);
}

.suggestion-list {
    background: var(--form-color) !important;
	border: 1px solid var(--border-color) !important;
}

.suggestion-list .selected {
    background: var(--darker-background-color) !important;
}

.suggestion-item[data-v-73dda3c2]:not(:last-of-type) {
    border-bottom: 1px solid var(--border-color);
}

.offcanvas-menu .dropdown-menu {
    background: var(--form-color) !important;
}

/* ------------- */
/* SETTINGS PAGE */
/* ------------- */
.settings-form {
    border: 0 !important;
    box-shadow: none !important;
    background: var(--main-background-color) !important;
}

.settings__content {
    background: var(--main-background-color) !important;
}

.main-header, .main-header__content {
    background: var(--main-background-color) !important;
    border-bottom: 1px solid var(--border-color) !important;
}

.settings__heading {
    color: var(--text-color) !important;
}

.field__label {
    color: var(--link-color) !important;
}

.settings-form__divider {
    border-top: 1px solid var(--border-color) !important;
}

.select__options {
    background: var(--main-background-color) !important;
    border: 1px solid var(--border-color) !important;
    color: var(--text-color);
}

.select__option:hover {
    background: var(--darker-background-color);
}

.input__element {
    border: 1px solid var(--border-color) !important;
}

.layout-card__content {
    background: var(--main-background-color) !important;
}

/* Settings burger hotfix */
.menu[data-v-528421d0] {
    background: var(--form-color) !important;
}

.list-item[data-v-528421d0]:hover {
    background: var(--darker-background-color) !important;
}

.link[data-v-528421d0], .link[data-v-528421d0]:hover, .link[data-v-528421d0]:visited, .link[data-v-528421d0]:active, .link[data-v-528421d0]:focus {
    background: transparent !important;
}

/* ------- */
/* SIGN-IN */
/* ------- */
.heading {
    color: var(--text-color) !important;
}

[aria-label="Ecosia logo"] > g > path:nth-child(2) {
    fill: white;
}

/* -------- */
/* HOMEPAGE */
/* -------- */
.title, .text, .claim-title, .claim-text {
    color: #CAC6BE;
}

.land-background[data-v-0a5129dd]::after {
    background: linear-gradient(rgba(25, 13, 13, 0) 80%, var(--border-color)) bottom no-repeat, url(https://cdn.ecosia.org/indexpage/img/5a18884.png) bottom repeat-x !important;
}

.notification {
    border-bottom: 1px solid var(--border-color) !important;
}

.background > path {
    fill: var(--darker-background-color);
}

.content {
    background: linear-gradient(var(--main-background-color), var(--main-background-color) 90%, hsla(0, 0%, 100%, 0)) bottom no-repeat !important;
}

.content[data-v-18ef2b10] {
    background: transparent !important;
}

.dropdown--side-right {
    border: 1px solid #5F5F5F !important;
}

.description, .text[data-v-4f0e76aa] {
    color: var(--text-color);
}

.personal-counter-dropdown {
    background: var(--form-color) !important;
    border: 1px solid var(--border-color) !important;
}

.personal-counter-content-actions {
    border-top: 1px solid var(--border-color) !important;
}

.link-more {
    background: var(--form-color) !important;
    border-top: none !important;
}

.link, .link:active, .link:focus, .link:visited {
    background: var(--form-color) !important;
}

.link:hover {
    background: #4D4D4D !important;
}

.suggestion-link[data-v-26502e69]:visited {
    color: #CAC6BE;
}

.suggestion-link[data-v-90260324], .suggestion-link[data-v-90260324]:active, .suggestion-link[data-v-90260324]:visited {
    color: var(--text-color) !important;
    background: var(--main-background-color);
}

.suggestion-link[data-v-90260324]:hover {
    color: var(--text-color) !important;
    background: var(--darker-background-color);
}

.suggestion-list[data-v-90260324] {
    border: 1px solid var(--darker-background-color);
}

.suggestion-item[data-v-90260324]:not(:last-of-type) {
    border-bottom: 1px solid var(--darker-background-color);
}

.link[data-v-16f3fc98]:hover {
    background: #4D4D4D;
}

.panel-header, .panel {
    background: var(--form-color) !important;
    border-bottom: 1px solid var(--border-color);
    border-left: 1px solid var(--border-color) !important;
}

.claim {
    box-shadow: 0 0 10px 0 rgba(255, 255, 255, 0.2) !important; 
}

/* Search bar */
.suggestion-link {
    color: var(--text-color) !important;
}

.suggestion-item {
    border-bottom: 1px solid var(--border-color) !important;
    background: var(--form-color) !important;
}

/* ------------------- */
/* MAIN STYLE ELEMENTS */
/* ------------------- */
.image-result {
    border: 2px solid var(--main-background-color);
}

nav[role="menu"] {
    color: white !important;
    background: var(--darker-background-color) !important;
}

.link[role="menuitem"] {
    background: var(--darker-background-color) !important;
}

input {
    background: var(--form-color) !important;
    color: var(--text-color) !important;
}

nav {
    background: var(--main-background-color) !important;
    color: white !important;
}

form, article {
    background: var(--form-color) !important;
    box-shadow: 0 0 4px 0 rgba(255, 255, 255, 0.2), 0 0 4px 0 rgba(255, 255, 255, 0.2) !important;
}

form[action="/search"], form[action="/images"], form[action="/news"], form[action="/videos"]  {	
    border: 0 !important;	
	box-shadow: none !important;	
}

footer {
    background: var(--main-background-color) !important;
    border-top: 1px solid #393939 !important;
}

p, h3 {
    color: var(--text-color) !important;
}

p > a {
    color: var(--link-color) !important;
}

main {
    background: var(--main-background-color) !important;
}

body, .row {
    background: var(--main-background-color);
}

::-moz-selection, ::selection {
    background: #696969;
}

/* -------------- */
/* ADVERTISEMENTS */
/* -------------- */
.product-ad {
    background: var(--main-background-color);
}

.ad-download, .ad-download:hover, .ad-download:focus, .ad-download:active,
.entity-disambiguation-title, .entity-disambiguation-title:hover, .entity-disambiguation-title:focus, .entity-disambiguation-title:active {
    color: var(--link-color);
}

.entity-disambiguation-container {
    box-shadow: 0 0 4px 0 rgba(255, 255, 255, 0.2), 0 0 4px 0 rgba(255, 255, 255, 0.2)
}

.entity-disambiguation-item {
    border-bottom: 1px solid var(--border-color);
}

.ad-download:visited, .entity-disambiguation-title:visited {
    color: var(--link-visited-color);
}

.sidebar-product-ad-title, .sidebar-product-ad-title:hover, .sidebar-product-ad-title:focus, .sidebar-product-ad-title:active {
    color: var(--link-color);
}

.sidebar-product-ad-title:visited {
    color: var(--link-visited-color);
}

.sidebar-product-ad-price {
    color: var(--text-color);
}

.sidebar-product-ad-seller-name {
    color: var(--link-green-color);
}

.sidebar-product-ad-item {
    border-bottom: 1px solid var(--border-color);
}

.funnel-treedaybox, .card-ad, .card-productads, .product-ad, .js-sidebar-ad {
    display: /*[[ads]]*/;
}
}

@-moz-document domain("info.ecosia.org") {
* {
    --main-color: #F7F7F7;
    --main-bg-color: #181A1B;
    --second-bg-color: #3F3F3F;
    --border-color: #5F5F5F;
    --color-one: #3dbfbc;
    --color-two: #7A8436;
    --color-three: #cb421f;
    scrollbar-color: #3F3F3F #1C1E1F;
}

.logo-anchor path:nth-child(2) {
    fill: white;
}

.a {
    color: var(--main-color) !important;
}

a.typeahead-link {
    background-color: var(--main-bg-color);
    color: var(--main-color);
}

.typeahead-suggestion, .typeahead-suggestion:not(:last-of-type) {
    border-bottom: 1px solid var(--border-color);
}

.typeahead {
    border: 1px solid var(--border-color);
}

div.lSSlideOuter {
    background-color: var(--color-two);
    color: var(--main-color);
}

div.offcanvas-menu-header {
    background-color: var(--second-bg-color);
}

nav.dropdown-menu.dropdown-menu-right {
    background-color: var(--main-bg-color);
}

.dropdown-menu-group {
    border-bottom: 1px solid var(--border-color);
}

label.js-offcanvas-menu-close.offcanvas-menu-close {
    background-color: none;
}

a.typeahead-link:hover {
    background-color: #2F2F2F;
    color: var(--main-color);
}

a.typeahead-link:visited {
    color: #AAAAAA;
}

h1.h1 {
    color: var(--main-color);
}

p.intro-text {
    color: var(--main-color);
}

p.custom-select-chosen.js-select-chosen {
    background-color: none;
    color: none;
}

