css:
@-moz-document url-prefix("https://jsos.pwr.edu.pl/") {
html {
    background: initial;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-color: #1c1c1e;
}

#size h1, #size .h1 {
    color: #8e8e93;
}

#size h3, #size .h3 {
    color: #8e8e93;
}
.col-lg-3,.span-19, .wrap-fluid, #content,#paper-bg {
	background-color: #2c2c2e;
}

.box .box-body {
    background-color: #3a3a3c;
}
.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th {
    background-color: initial;
}
.table {
    color: white;
}
.box .box-header > .fa, .box .box-header > .glyphicon, .box .box-header > .ion, .box .box-header .box-title span {
    color: #8e8e93;
}

.box .box-header {
    background: #3a3a3c;
}
.box-header > .glyphicon, .box .box-header > .ion, .box .box-header .box-title i {
    background: #8e8e93;
}
.well {
    background-color: #48484a !important;
}
label {
    color: white;
}
.well legend {
    color: #8e8e93;
}

#paper-top {
    background-color: #2c2c2e;
}
.box .box-body {
    color: white;
}
.table {
    background-color: #48484a;
    color: white;
}
#content-mail {
    background: none repeat scroll 0 0 #3a3a3c;
}
h4 {
    color: #8e8e93 !important;
}
div.breadcrumbs {
    background-color: #3a3a3c;
}
.sub-board {
    border-bottom: 1px solid #3a3a3c;
    border-top: 3px solid #3a3a3c;
}
.sub-board {
    background: #3a3a3c;
}
.sbSelector:link, .sbSelector:visited, .sbSelector:hover {
    color: white;
}
a {
    color: white;
}
.box {
    background: #3a3a3c !important;
}
}
