css:
@-moz-document url-prefix("https://ak.kif.rocks/kif475/plan/") {
table tbody td:first-child {
    position: sticky;
    left: 0;
    z-index: 10;
}

table tbody tr:hover {
    background: hsla(0deg, 0%, 50%, .42);
}

#nowslider {
    background: linear-gradient(to right, rgba(100,255,255,0.0) 0%,#f07 100%);
}
}
