css:
body {
    background: url(https://www.toptal.com/designers/subtlepatterns/patterns/dot-grid.png);
    background-repeat:repeat;
}
th, td {
    border: 2px solid #d0d0d0;
}
td {
    text-align: center;
    background-color: white;
}
th {
    background-color: #8B8878;
    color: #FFF8DC;
    text-align: center;
    border-bottom: none;
    border-top: none;
    font-size: 1.7em;
}
table {
    margin: 0 auto;
    width: 100%;
    max-width: 750px;
}
tbody {
    margin: 0 auto;
    width: 100%;
}
