css:
@-moz-document domain("gitter.im") {
header.header {
    height: 42px !important;
}

section.layout-minibar {
    width: 5.2rem !important;
}

.item-toggle .icon-wrapper {
    width: 20px !important;
}

.chat-header {
    height: 42px;
}

.chat-header__main-grouping {
    height: 42px;
}

.chat-and-toolbar-wrapper {
    padding-top: 42px;
}


.chat-header__heading {
    margin-top: 0;
}

.chat-header__topic:not(:empty) {
    margin-top: 9px;
}

.chat-header__title {
    font-size: 22px;
}

.chat-header__group-avatar-upload-label {
    width: 30px;
    height: 30px;
    margin-top: 3px;
}

.chat-header__action-grouping .profile-menu {
    padding-top: 5px;
}

.right-toolbar-region .right-toolbar__body {
    padding-top: 0px;
}

.chat-header__action-grouping .dropdown.profile-menu__list {
    top: 37px;
}

.chat-header__org-page, .chat-header__favourite, .chat-header__settings, .chat-header__right-toolbar-toggle {
    font-size: 20px;
}

.chat-header__action-icon-svg--settings {
    width: 20px;
}

.list-item.is-favourited:before {
    left: 0.5rem !important;
}

.menu-bar-top .item, .menu-bar-bottom .item {
    height: 50px;
}

.unpinned:not(.mobile) .layout-main-menu {
    left: 5.2rem !important;
}

.room-list-title {
    line-height: 50px !important;
}

.right-toolbar-region__footer {
    padding-top: 32px;
}

.right-toolbar-region__footer .menu-toggle-icon {
    width: 25px;
    height: 26px;
}

}
