css:
@-moz-document domain("americantrucksimulator.pl") {
.ats-post,.ats-blockcontent,.ats-pager{
    background: #ffffff40;
}
.ats-postcontent a, .ats-postcontent a:link{
    color: #fff;
}
 #ats-main{
    background-size: cover;
}
}

@-moz-document url-prefix("http://eurotrucksimulator2.info") {
.ets2-layout-wrapper,.ets2-sheet{
    background: #000;
}
.ets2-post,.ets2-blockcontent{
    background: #545454
}
.ets2-post,.ets2-post ul li{
    color: #918e8e;
}
}
