/* Invert the whole website */
html {
    filter: invert(0.80) hue-rotate(180deg) contrast(1.2) !important;
    background: #ffffff !important;
}

/* Ensure the entire height of the content has the right base background */
div.sc-cvbbAY, .lbcZiO {
    background: #ffffff !important;
}

/* images, icons, avatars, emojis */
img, span[role="img"], .emoji-common-emoji-sprite {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05) !important;
}

/* sidebar icons */
.css-xbia9 svg {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05) !important;
}

/* sidebar */
.css-gim7cw, .css-t7y1k0.css-t7y1k0, .css-1x95dtn.css-1x95dtn {
    background-color: #ffffff !important;
}

/* sidebar highlights */
.css-vmviqs.css-vmviqs, .css-1uo8dyi.css-1uo8dyi {
    background-color: #aaaaaa !important;
}

/* darken background on popup */
.dKnSu {
    background: rgba(255, 255, 255, 0.7)
}
}
