css:
@-moz-document url-prefix("https://www.steamgifts.com/") {
.esgst-popup-layer {
    align-items: center;
    bottom: 0;
    display: none;
    justify-content: center;
    left: 0;
    padding: 50px;
    position: fixed;
    right: 0;
    top: 0;
}

.esgst-popup-modal {
    background-color: rgba(60, 66, 77, 0.7);
    bottom: 0;
    cursor: pointer;
    left: 0;
    position: fixed;
    right: 0;
    top: 0;
    display: none;
}

.esgst-popup {
    background-color: var(--SG-Dark-Grey-body-bg-color)!important;
    box-shadow: 0 0 0 1px transparent!important;
    border: solid 1px var(--SG-Dark-Grey-content-border-color);
    color: hsla(0,0%,65%,0.95)!important;
    padding: 25px 65px!important;
    text-shadow: 1px 1px hsla(0,0%,0%,0.99),0 0 1px hsla(0,0%,0%,0.99)!important;
    max-width: calc(90% - 40px);
    display: none;
}


}
