css:
@-moz-document domain(es6.ruanyifeng.com) {

/*positon of markdown content*/
body {
    background-color: #E3EDCD !important;
}
#content {
    width: 75% !important;
    padding-left: 350px !important;
}

/*useless button*/
#edit,
#back_to_top,
#flip {
    display: none !important;
}

/*ads*/
a div {
    display: none !important;
}

/*jump to head*/
.section-link + a {
    display: none !important;
}
input {
    background-color: #E3EDCD !important;
}

.searchButton {
    background-color: #E3EDCD !important;
    opacity:0.3;
}

#sidebar{
    overflow:auto 
}

#sidebar ul{
    display: none !important;
}
ol+h2{
    display: none !important;
}

}
