css:
@-moz-document domain("www.chip.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #D1181E #1C1C1C;
}
#main{
    background: #3b3b3b;
}
.timeline{
    border-left: none;
}
.timeline .entry{
    margin-left: 0;
}
.timeline h2 a,.timeline .subtitle:not(:focus):not(:hover),.timeline .comments{
    color: #fff;
}
.tax-widget.ranking.featured .title a{
    color: #ED1D23;
}
.time time,.latest a{
    color: #fff;
}
/*ARTYKU≈Å*/
.excerpt{
    color: #ED1D23;
}
.content p{
    color: #fff;
}
.content h3,.content h2{
    color: #ED1D23;
}
.marks span{
    color: #ED1D23;
}
/*AUTOR*/
.main-heading{
    color: #ED1D23;
}
.listing .title a{
    color: #ED1D23;
}
.listing .title a:hover{
    color: #fff;
}
.listing .excerpt{
    color: #fff;
}
/*RANKING*/
.letters-widget.hide-missing a{
    color: #ED1D23;
}
.letters-widget.hide-missing a:hover{
    color: #fff;
}
#main-header .search-overlay{
    background: #3b3b3b;
}
#main-header .search-overlay input{
    background: #505050;
}
#main-footer .menu:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #a6a6a6;
}
}
